import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { View, Text, Image } from 'react-native';
import Index from "./screens/Index";
import Details from "./screens/Details";
import Create from "./screens/Create";
import Edit from "./screens/Edit";
import Delete from "./screens/Delete";
import {
  DefaultTheme,
  Provider as PaperProvider,
  // PaperDefaultTheme,
  // NavigationDefaultTheme,
} from "react-native-paper";

import { React } from "react";

function LogoTitle() {
  return (
    <Image
      style={{ width: 50, height: 50 }}
      source={require('require("./assets/ROI-150px.png")')}
    />
  );
}
const Stack = createNativeStackNavigator();
export default function App(props) {
  let theme = {
    ...DefaultTheme,
    roundness: 6,
    mode: "outlined",
    colors: {
      ...DefaultTheme.colors,
      primary: "#941a1d",
      accent: "#262626"},

    };

  return (
    // <PreferencesContext.Provider value={preferences}>
    <PaperProvider>
      <NavigationContainer>
        <Stack.Navigator>
          <Stack.Screen
            name="Index"
            component={Index}
            options={{ LogoTitle , DefaultTheme, title: "Employee Directory" }}
          />
          <Stack.Screen
            name="Create"
            component={Create}
            options={{ DefaultTheme, title: "Add Employee" }}
          />
          <Stack.Screen
            name="Details"
            component={Details}
            options={{ DefaultTheme, title: "Employee Details" }}
          />
          <Stack.Screen
            name="Edit"
            component={Edit}
            options={{ DefaultTheme, title: "Update Employee Details" }}
          />
          <Stack.Screen
            name="Delete"
            component={Delete}
            options={{ DefaultTheme, title: "Delete Employee" }}
          />
        </Stack.Navigator>
      </NavigationContainer>
    </PaperProvider>
    // </PreferencesContext.Provider>
  );
}
