  //   [toggleTheme, isThemeDark]
  // );
  let theme = {
    ...DefaultTheme,
    roundness: 6,
    mode: "outlined",
    colors: {
      ...DefaultTheme.colors,
      primary: "#941a1d",
      accent: "#262626"},

    };

  return (
    // <PreferencesContext.Provider value={preferences}>
    <PaperProvider>
      <NavigationContainer>
        <Stack.Navigator>
          <Stack.Screen
            name="Index"
            component={Index}
            options={{ headerTitle: () => (
              <Image style={{ width: 50, height: 50 }} source={require("./assets/ROI-150px.png")} />
            ), DefaultTheme, title: "Employee Directory" }}
          />
          <Stack.Screen
            name="Create"
            component={Create}
            options={{ DefaultTheme, title: "Add Employee" }}
          />
          <Stack.Screen
            name="Details"
            component={Details}
            options={{ DefaultTheme, title: "Employee Details" }}
          />
          <Stack.Screen
            name="Edit"
            component={Edit}
            options={{ DefaultTheme, title: "Update Employee Details" }}
          />
          <Stack.Screen
            name="Delete"
            component={Delete}
            options={{ DefaultTheme, title: "Delete Employee" }}
          />
        </Stack.Navigator>
      </NavigationContainer>

    </PaperProvider>
    // </PreferencesContext.Provider>
  );
}
